mapper.select:


     and title like concat('%', #{title}, '%')
     and description like concat('%', #{description}, '%')
     and image_url like concat('%', #{imageUrl}, '%')
     and is_show = #{isShow}
     and sort_order = #{sortOrder}

order by sort_order asc, create_time desc

mapper.update:
update txwx_carousel_image_temp
image_url = #{imageUrl}, is_show = #{isShow}, sort_order = #{sortOrder}, title = #{title}, description = #{description}, jump_url = #{jumpUrl}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, saved_by = #{savedBy}, saved_time = #{savedTime}, published_by = #{publishedBy}, published_time = #{publishedTime}, remark = #{remark},
where carousel_id = #{carouselId}

mapper.insert:
insert into txwx_carousel_image_temp (
    title, description, image_url, is_show, jump_url, sort_order, create_by, create_time, 
    update_by, update_time, saved_by, saved_time, published_by, published_time, remark
)
values

    (
        #{item.title}, #{item.description}, #{item.imageUrl}, #{item.isShow}, #{item.jumpUrl}, #{item.sortOrder},
        #{item.createBy}, #{item.createTime}, #{item.updateBy}, #{item.updateTime}, 
        #{item.savedBy}, #{item.savedTime}, #{item.publishedBy}, #{item.publishedTime}, #{item.remark}
    )
