mapper.select:


     and product_name like concat('%', #{productName}, '%')
     and description like concat('%', #{description}, '%')
     and image_url like concat('%', #{imageUrl}, '%')
     and is_show = #{isShow}
     and sort_order = #{sortOrder}

order by sort_order asc, create_time desc

mapper.update:
update txwx_main_product_temp
product_name = #{productName}, image_url = #{imageUrl}, description = #{description}, is_show = #{isShow}, sort_order = #{sortOrder}, jump_url = #{jumpUrl}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, saved_by = #{savedBy}, saved_time = #{savedTime}, published_by = #{publishedBy}, published_time = #{publishedTime}, remark = #{remark},
where product_id = #{productId}

mapper.insert:
insert into txwx_main_product_temp (
    product_name, description, image_url, is_show, sort_order, jump_url, create_by, create_time, 
    update_by, update_time, saved_by, saved_time, published_by, published_time, remark
)
values

    (
        #{item.productName}, #{item.description}, #{item.imageUrl}, #{item.isShow}, #{item.sortOrder}, #{item.jumpUrl}, 
        #{item.createBy}, #{item.createTime}, #{item.updateBy}, #{item.updateTime}, 
        #{item.savedBy}, #{item.savedTime}, #{item.publishedBy}, #{item.publishedTime}, #{item.remark}
    )
