mapper.select:


     and customer_name like concat('%', #{customerName}, '%')
     and image_url like concat('%', #{imageUrl}, '%')
     and jump_url like concat('%', #{jumpUrl}, '%')
     and is_show = #{isShow}
     and sort_order = #{sortOrder}

order by sort_order asc, create_time desc

mapper.update:
update txwx_typical_customer_temp
customer_name = #{customerName}, image_url = #{imageUrl}, jump_url = #{jumpUrl}, is_show = #{isShow}, sort_order = #{sortOrder}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, saved_by = #{savedBy}, saved_time = #{savedTime}, published_by = #{publishedBy}, published_time = #{publishedTime}, remark = #{remark},
where customer_id = #{customerId}

mapper.insert:
insert into txwx_typical_customer_temp (
    customer_name, image_url, jump_url, is_show, sort_order, create_by, create_time, 
    update_by, update_time, saved_by, saved_time, published_by, published_time, remark
)
values

    (
        #{item.customerName}, #{item.imageUrl}, #{item.jumpUrl}, #{item.isShow}, #{item.sortOrder}, 
        #{item.createBy}, #{item.createTime}, #{item.updateBy}, #{item.updateTime}, 
        #{item.savedBy}, #{item.savedTime}, #{item.publishedBy}, #{item.publishedTime}, #{item.remark}
    )
